  registerAccount(type: string, credentials?: Dict<string>){

    if (type == "Guest"){
      this.auth.signInAnonymously().then(() => {
        // Signed in..

        if (this.myCallback)
          this.myCallback()

        }).catch((error) => {
          var errorCode = error.code;
          var errorMessage = error.message;

          if (this.errCallback)
          this.errCallback(errorMessage)
        });
    }

    else if(type == "Email_UP"){

      let username = credentials?.username
      let password = credentials?.password!
      let email = credentials?.email!

      this.auth.createUserWithEmailAndPassword(email, password).then(async (result) => {
        // Signed in..
        await result.user?.sendEmailVerification()
        if (result.user && username){
          await this.setUsername(result.user?.uid, username, true)
          if (this.myCallback)
          this.myCallback()
        }
        else{
          await this.auth.signOut()
          if (this.myCallback)
          this.myCallback()
        }
        }).catch((error) => {
          var errorCode = error.code;
          var errorMessage = error.message;

          if (this.errCallback)
          this.errCallback(errorMessage)

          console.log(errorCode)

      });  
    }
    else if(type == "Email_IN"){
      let password = credentials?.password!
      let email = credentials?.email!

      this.auth.signInWithEmailAndPassword(email, password).then(async (result) => {
        // Signed in..
        if (result.user){
          if (this.myCallback)
          this.myCallback()
        }
        else{
          await this.auth.signOut()
          if (this.myCallback)
          this.myCallback()
        }
        }).catch((error) => {
          var errorCode = error.code;
          var errorMessage = error.message;

          if (this.errCallback)
          this.errCallback(errorMessage)

          console.log(errorCode)
      }); 
    }
    else if(type == "Email_IN_USER"){

      let data = {
        "Field" : credentials?.Field,
        "Term" : credentials?.Term
      }
      this.functions.httpsCallable('auth')(data)
      .pipe(first())
      .subscribe(async resp => {

        if (resp as string){
          if (resp.includes("ERROR:")){
            let err = resp.replace("ERROR:", "")
            console.log(err)
          }
          else{

            let email = resp as string
            let password = credentials?.password!

            this.auth.signInWithEmailAndPassword(email, password).then(async (result) => {
              // Signed in..
              if (result.user){
                if (this.myCallback)
                this.myCallback()
              }
              else{
                await this.auth.signOut()
                if (this.myCallback)
                this.myCallback()
              }
              }).catch((error) => {
                var errorCode = error.code;
                var errorMessage = error.message;

                if (this.errCallback)
                this.errCallback(errorMessage)

                console.log(errorCode)
            }); 
          }
        }
        else {

        }
      }, err => {
        var errorCode = err.code;
        var errorMessage = err.message;
        if (this.errCallback)
          this.errCallback(errorMessage)
        console.log(errorCode)
      });
    }
    else if (type == "Apple"){
      var appleProvider = new firebase.auth.OAuthProvider('apple.com');
      appleProvider.addScope('email');
      appleProvider.addScope('name');

      this.auth.signInWithPopup(appleProvider).then(async (result) => {
        // Signed in..

        if ((result.additionalUserInfo?.isNewUser ?? false) && result.user?.uid){
          await result.user?.sendEmailVerification()
          await this.setUsername(result.user?.uid, result?.additionalUserInfo?.username ?? 'user_' + uuid.toString(), (!(this.isUndefined(result?.additionalUserInfo?.username))))
        }
        if (this.myCallback)
          this.myCallback()

        }).catch((error) => {
          var errorCode = error.code;
          var errorMessage = error.message;

          if (this.errCallback)
          this.errCallback(errorMessage)
        });
    }
    else if (type == "Google"){
      var googleProvider = new firebase.auth.GoogleAuthProvider();
      googleProvider.addScope('email');
      googleProvider.addScope('profile');

      this.auth.signInWithPopup(googleProvider).then(async (result) => {
        // Signed in..
        if ((result.additionalUserInfo?.isNewUser ?? false) && result.user?.uid){
          // await result.user?.sendEmailVerification()
          await this.setUsername(result.user?.uid, result?.additionalUserInfo?.username ?? 'user_' + uuid().replace('-', ''), (!(this.isUndefined(result?.additionalUserInfo?.username))))
        }
        if (this.myCallback)
          this.myCallback()

        }).catch((error) => {
          var errorCode = error.code;
          var errorMessage = error.message;

          console.log(error)
          if (this.errCallback)
          this.errCallback(errorMessage)
        });
    }
  }


