  getPost(productID: string, forCart?: boolean, cartIndex?: number, forOrder?: boolean, orderIndex?: number, productIndex?: number, isSelectedOrder?: boolean, forSale?: boolean){

    let sub = this.db.collectionGroup("Products", ref => ref.where("Product_ID",'==', productID).where("Public",'==', true)).valueChanges().subscribe((docDatas) => {

      if (docDatas.length == 0){
        if (this.myCallback) 
          this.myCallback()
        if (isPlatformBrowser(this.platformID))
          sub.unsubscribe();
      }

      docDatas.forEach((doc) => {
        const docData = doc as DocumentData
        if (docData){
          let uid = docData.UID as string
          let productID = docData.Product_ID as string
          let timestamp = (docData.Timestamp as firebase.firestore.Timestamp).toDate()
          let description = docData.Description as string
          let name = docData.Name as string

          let blurred = docData.Blurred as boolean
          let templateColor = docData.Template_Color as string

          let likes = docData.Likes as number

          let comments = docData.Comments as number
          let isPublic = docData.Public as boolean ?? true
          let productType = docData.Type as string ?? "ATC1000"
          let displaySide = docData.Side as string ?? "front"
          let sides = docData["Sides"] as Array<string> ?? ["Front"]
          let priceCents = docData.Price_Cents as number

          let product = new Product(
            uid, 
            productID, 
            description, 
            productID, 
            timestamp, 
            "", 
            blurred, 
            priceCents,
            name, 
            templateColor, 
            likes, 
            false, 
            comments, 
            true, 
            isPublic, 
            productType, 
            displaySide, 
            sides,
            this.getURL(uid, productID)
          )

          if ((forCart ?? false)){
            Globals.cart![cartIndex!].product = product
            if (this.myCallback) 
              this.myCallback()
            if (isPlatformBrowser(this.platformID))
              sub.unsubscribe();
          }
          else if ((forOrder ?? false)){
            product.price = Globals.orders![orderIndex!].products[productIndex!].product?.price ?? 0
            Globals.orders![orderIndex!].products[productIndex!].product = product
            if (this.myCallback) 
              this.myCallback()
            if (isPlatformBrowser(this.platformID))
              sub.unsubscribe();
          }
          else if ((isSelectedOrder ?? false)){
            product.price = Globals.selectedOrder!.products[productIndex!].product?.price ?? 0
            Globals.selectedOrder!.products[productIndex!].product = product
            if (this.myCallback) 
              this.myCallback()
            if (isPlatformBrowser(this.platformID))
              sub.unsubscribe();
          }
          else if (forSale && productIndex && Globals.productsSold){
            Globals.productsSold![productIndex] = product;
          }
          else{
            Globals.selectedProduct = product;
            if (this.myCallback) 
              this.myCallback()
            if (isPlatformBrowser(this.platformID))
              sub.unsubscribe();
          }
        }
      })

    });
  }

